                        >
                          <Edit className="w-4 h-4 mr-2" />
                          Edit
                        </DropdownMenuItem>
                        <DropdownMenuItem 
                          onClick={() => handleDownloadPDF(invoice)}
                          className="flex items-center px-3 py-2 hover:bg-blue-50 text-gray-700 hover:text-blue-600 rounded-md transition-colors duration-150"
                        >
